<?php		header("Content-Type:text/html;charset=utf-8");	include("connect.php");	$l_name = $_POST[l_name];	$uptypes = array('application/vnd.ms-excel');	$max_file_size = 20000000;	$destination_folder = '../sci_factor/';	if ($_SERVER['REQUEST_METHOD'] == 'POST') {		$info = '';		$name = null;		if (is_uploaded_file($_FILES['l_content']['tmp_name'])) {			$upfile = $_FILES['l_content'];			$name = time().$FILES['name'];			$type = $upfile['type'];						$size = $upfile['size'];			$tmp_name = $upfile['tmp_name'];			$error = $upfile['error'];			if($max_file_size<$size) {				$info='上传文件太大！不能上传！';			}			/*			if (!in_array($type,$uptypes)) {				$info = $info.' 上传文件类型不符'.$type.'不能上传';			}			*/			$type = trim(substr($type,6,12));			if ($info == '') {			    $name .= '.xls';				if (move_uploaded_file($_FILES['l_content']['tmp_name'], $destination_folder.$name)) {					$info='上传文件成功！';					$l_content = "../background/sci_factor/$name";				} else {					$name=null;				}								$sql = "insert into link (l_name,l_content,l_mark) values('$l_name','$l_content','2')";				mysql_query($sql);				$flag = mysql_affected_rows();				if($flag == 1){				    $info .= '添加成功！';				} else {				    $info .= '添加失败！';					unlink($name);				}				echo "<script>alert('$info');parent.window.location.href='../view/add_factor.php';</script>";			} else {		        $info = '上传文件失败！';			    echo "<script>alert('$info');</script>";		    }		} else {		    $info = '未选择文件！';			echo "<script>alert('$info');</script>";		}			}?>