<?php		header("Content-Type:text/html;charset=utf-8");	include("connect.php");	$uptypes = array('image/jpg', 'image/png', 'image/jpeg', 'image/pjpeg', 'image/gif',	'image/bmp', 'image/x-png');	$max_file_size = 20000000;	$destination_folder = '../picture/people/';	if ($_SERVER['REQUEST_METHOD'] == 'POST') {		$info = '';		$name = null;		if (is_uploaded_file($_FILES['img']['tmp_name'])) {			$upfile = $_FILES['img'];			$name = time().$FILES['name'];			$type = $upfile['type'];			$size = $upfile['size'];			$tmp_name = $upfile['tmp_name'];			$error = $upfile['error'];			if($max_file_size<$size) {				$info='上传图片太大！不能上传！';			}			if (!in_array($type,$uptypes)) {				$info = $info.' 上传图片类型不符'.$type.'不能上传';			}			$type = trim(substr($type,6,12));			if ($info == '') {				//echo $destination_folder.$name.'.'.$type;				if (move_uploaded_file($_FILES['img']['tmp_name'], $destination_folder.$name. '.' .$type)) {					$info='上传图片成功！';					$name=$destination_folder.$name.'.'.$type;				} else {					$name=null;				}			}		}		$password = sha1("123456");		$sql="insert into member(m_username, m_cname,m_ename,m_password,m_permit,m_title,m_mark,m_email,m_tel,m_addr,m_photopath,m_detail) values('$_POST[ename]','$_POST[cname]','$_POST[ename]','$password','$_POST[permit]','$_POST[title]','$_POST[mark]','$_POST[email]','$_POST[tel]','$_POST[office]','$name','$_POST[details]')";        mysql_query($sql);		$flag = mysql_affected_rows();		if($flag == 1){		    echo "<script>alert('添加成功！');window.location.href='../view/add_member.php';</script>";		} else {    		unlink($name);		    echo "<script>alert('添加失败！');window.location.href='../view/add_member.php';</script>";		}	}?>