m>		</div>	</div>	<?php include('./include/footer.php');?></div><iframe width='0' height='0' name='iframe'></iframe><script type="text/javascript" charset="utf-8" >$(function(){    $('#attachment_manage').dialog({					autoOpen: false,					width: 800,					buttons: {						"Ok": function() { 							$(this).dialog("close"); 						}, 						"Cancel": function() { 							$(this).dialog("close"); 						} 					},		            modal: true				});})function showContent() {    $('#attachment_manage').dialog('open');    editor.sync();	var i = 0;	var html = "<div id='attach_items'>";	$('iframe').contents().find("img").each(function(i) {	    var src = $(this).attr("src");		var index = src.lastIndexOf("/") + 1;	    var fileName = src.substr(index);		var id = "attach" + i;	    html = html + "<div class='attach_item' id='"+id+"' style='float:left;margin:10px'>";		html = html + "<a href='" + src + "' target='_blank'>" + "<img title='"+fileName+"' height='140px' src='" + src + "'>" + "</img></a>";		html = html + "<br/><a href='javascript:deleteAttachment(\"" + src + "\",\""+id+"\")'>"+'删除'+"</a> | <a href='javascript:setSlide(\"" + src + "\",\""+id+"\")'>设置为首页幻灯片</a>";		html = html + "</div>";		i++;	});	var html = html +  "<div stlye='clear:both'></div>";	var html = html +  "</div>";	var html = html +  "</table>";	$('#attachment_manage').html(html);}function attachment() {    var url;}function deleteAttachment(oriUrl, id) {    var index = oriUrl.indexOf('/background');	index += '/background'.length;	url = ".." + oriUrl.substr(index);    if (confirm("确定删除？")) {	    var data1 = new attachment();		data1.url = url;	    $.ajax({		    type: 'POST',			url: "../model/delete_attachment.php",			data: data1,			success: function(res) {			    if (res == "1") {				    alert("succeed!");					$("#"+id).remove();					$('iframe').contents().find("[src='"+oriUrl+"']").remove();				} else {				    alert("failed!");				}			}		});	};}function data() {    var id;	var name;	var content;	var slide_pic;}function submit() {    var data1 = new data();	data1.id = $("#id").val();	data1.name = $("#name").val();	data1.content = $("#editor1").val();	data1.slide_pic = $("#slide_pic").val();    $.ajax({	    type: 'POST',		url: "../model/modify_lab.php",		data: data1,		success: function(res) {		    if (res == "1") {			    alert("succeed!");				window.location.reload();			} else {			    alert("failed!");			}		}	});}function setSlide(oriUrl, id) {    var index = oriUrl.indexOf('/background');	index += '/background'.length;	url = ".." + oriUrl.substr(index);	$("#slide_pic_img").attr('src', url);	$("#slide_pic").val(url);}</script><script type="text/javascript">function check(){    editor.sync();	if(document.getElementById('name').value==""){		alert("名称空！");		return false;	} else {	    submit();	}	return true;}</script></body></html>